    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String course = (String)Box1.getSelectedItem();
        String branchName = (String)Box2.getSelectedItem();
        String roll = jTextField1.getText();
        String name = jTextField2.getText();
        String gender = (String)jComboBox3.getSelectedItem();
        String fatherName = jTextField3.getText();
        
        // Check if Roll No and Name fields are empty
        if (roll.isEmpty() || name.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Please fill in all details", "Error", JOptionPane.ERROR_MESSAGE);
        }
        else {
            //calling the drivevr class of sql
            //saying thar i want connection with you

            //Connecting with database
            try {
                Class.forName("com.mysql.cj.jdbc.Driver");       
                Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/srms", "root", "P@Hole02");

                //use statement class
                Statement st = con.createStatement();
                st.executeUpdate("insert into student(rollno, course, branchName, name, gender, fathername) values('"+roll+"', '"+course+"', '"+branchName+"', '"+name+"', '"+gender+"', '"+fatherName+"')");
                JOptionPane.showMessageDialog(null, "Data Saved Succesfully");
                setVisible(false);
                new adminHome().setVisible(true);
            }
            catch(Exception e) {
                 JOptionPane.showMessageDialog(null, e.toString());
            }
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

